package com.let.exercises.aoc2017;

public class day01 {
	public static void main(String[] args){
		System.out.println(sumNext("1122"));
		System.out.println(sumNext("1111"));
		System.out.println(sumNext("91212129"));
		System.out.println(sumNext(""));

		System.out.println(sumHalfwayAround("1212"));
		System.out.println(sumHalfwayAround("1221"));
		System.out.println(sumHalfwayAround("123425"));
		System.out.println(sumHalfwayAround("123123"));
		System.out.println(sumHalfwayAround("12131415"));
		System.out.println(sumHalfwayAround(""));
	}

	public static Integer sumNext(String value){
		Integer sum = 0;

		String[] arr = value.split("");

		if (arr[0].equals(arr[arr.length-1])) sum = Integer.valueOf(arr[arr.length-1]);

		for (int i = 0; i < arr.length-1; i++) {
			if (arr[i].equals(arr[i+1]))
				sum += Integer.valueOf(arr[i]);
		}

		return sum;
	}

	public static Integer sumHalfwayAround(String value){
		Integer sum = 0;

		String[] arr = value.split("");

		int halfway = arr.length / 2;

		for (int i = 0; i < arr.length; i++) {
			if (i < halfway ) {
				if (arr[i].equals(arr[i + halfway]))
					sum += Integer.valueOf(arr[i]);
			}
			else {
				if (arr[i].equals(arr[i - halfway]))
					sum += Integer.valueOf(arr[i]);
			}
		}

		return sum;
	}

}
